import basicImg from "../../public/basic";
import hatImg from "/hat.jpg";
import shirtImg from "/shirt.jpg";
import standardImg from "/standard.jpg";
import premiumImg from "/premium.jpg";

export function getImage(id) {
      if (id === 1) {
          return basicImg;
      }
      if (id === 2) {
          return standardImg;
      }
      if (id === 3) {
        return premiumImg;
      }
      if (id === 4) {
          return hatImg;
      }
          return shirtImg;
      }
